    private static String encodeBase64ByFilePath(String path) throws IOException {
        File file = new File(path);
        return encodeBase64ByFile(file);
    }
    private static String encodeBase64ByFile(File file) throws IOException{
        FileInputStream inputFile = new FileInputStream(file);
        byte[] buffer = new byte[(int)file.length()];
        inputFile.read(buffer);
        inputFile.close();
        Base64.Encoder encoder  = Base64.getEncoder();
        return encoder.encodeToString(buffer);
    }

    private static void setRequestParams(HttpEntityEnclosingRequestBase request) {
        String nonce = UUID.randomUUID().toString().replace("-", "");
        Long timestamp = System.currentTimeMillis() / 1000;

        request.setHeader("Api-Auth-pubkey", credentials.getPubkey());
        request.setHeader("Api-Auth-nonce", nonce);
        request.setHeader("Api-Auth-timestamp", String.valueOf(timestamp));
        request.setHeader("Api-Auth-sign", getSign(nonce, timestamp, credentials.getSecret()));
        request.setHeader("Content-Type", CONTENT_TYPE);
        System.out.println(JSONObject.toJSON(request.getAllHeaders()).toString());
    }

    private static String getSign(String nonce, Long timeStamp, String secret) {
        String source = nonce + timeStamp + secret;
        StringBuilder buffer = new StringBuilder();
        try {
            MessageDigest sha = (MessageDigest) md.clone();
            sha.update(source.getBytes());
            for (byte b : sha.digest()) {
                buffer.append(String.format("%02X", b));
            }
            return buffer.toString().toLowerCase();
        } catch (CloneNotSupportedException e) {
            logger.error(e.getMessage());
            return "";
        }
    }
